import Foundation
import NaturalLanguage

internal var stopwords = stopwordsEn

@available(OSX 10.14, *)
internal func setStopwordLanguage(to language: NLLanguage) {
    switch language {
    case .english:
        stopwords = stopwordsEn
    case .german:
        stopwords = stopwordsDe
    case .portuguese:
        stopwords = stopwordsPt
    default:
        stopwords = stopwordsEn
    }
}

